mapper.select:
select 
ct.*
from customer_tag ct
where ct.import_batch=#{importBatch}
limit #{pageItemNo},#{itemCount}

       SELECT
	ct.*
	FROM
	call_record cr,customer_tag ct
	WHERE
	cr.customer_tag_id = ct.id
	AND ct.import_batch = #{importBatch}
	and cr.status=#{status}

      and cr.name like '%${name}%'


      and cr.customer_server_nick_name like '%${customerServerNickName}%'


      and cr.call_result =#{callResult}


      and ct.dial_time =#{dialTime}


      and cr.follow_date is not null and  DATEDIFF(cr.follow_date,now())=#{followDay} 



      and timestampdiff(SECOND,cr.start_call_time,cr.end_call_time) <= #{callTime}




      and cr.create_time>=#{startDate}




       and cr.create_time <= #{endDate}


    GROUP BY ct.id
    limit #{pageItemNo},#{itemCount}

       SELECT
	COUNT(DISTINCT cr.customer_tag_id)
	FROM
	call_record cr,customer_tag ct
	WHERE
	cr.customer_tag_id = ct.id
	AND ct.import_batch = #{importBatch}
	and cr.status=#{status}

      and cr.name like '%${name}%'


      and cr.customer_server_nick_name like '%${customerServerNickName}%'


      and cr.call_result =#{callResult}


      and ct.dial_time =#{dialTime}


      and  DATEDIFF(cr.follow_date,now())=#{followDay}



      and timestampdiff(SECOND,cr.start_call_time,cr.end_call_time) >= #{callTime}




      and cr.create_time>=#{startDate}




       and cr.create_time <= #{endDate}

       SELECT
       ct.id,
	ct.is_repeate,
	ct.dial_time,
	IFNULL(ct.customer_tag,"") as customer_tag
	FROM
	call_record cr,customer_tag ct
	WHERE
	cr.customer_tag_id = ct.id
	AND ct.import_batch = #{importBatch}
	and cr.status=#{status}

      and cr.name like '%${name}%'


      and cr.customer_server_nick_name like '%${customerServerNickName}%'


      and cr.call_result =#{callResult}


      and ct.dial_time =#{dialTime}


      and  DATEDIFF(cr.follow_date,now())=#{followDay}



      and timestampdiff(SECOND,cr.start_call_time,cr.end_call_time) >= #{followDay}




      and cr.create_time>=#{startDate}




       and cr.create_time <= #{endDate}


    GROUP BY ct.id

SELECT ct.* FROM call_record cr,customer_tag ct WHERE cr.customer_tag_id = ct.id AND ct.import_batch = #{importBatch}
and cr.customer_server_nick_name like '%${customerServerNickName}%'
and cr.call_result =#{callResult}
GROUP BY ct.id limit #{pageItemNo},#{itemCount}
